import React from 'react'
import { Button } from '@material-ui/core'
 class Main extends React.Component{
    
     render(){
        const style ={
            backgroundColor: "#f5dfb8",
                width: 270,
                border: "2px solid grey",
                padding: 10,
                margin: 3,
                display : "block",
                float: "left"
          
    }
   function Trailer()
    {
        return(
            <div>
            <video>{this.props.movie.Trailer}</video>
        </div>)
    }
        return (
            <div className ="Movie-names" style={style}>
                <img src={this.props.movie.imgUrl}width ="250" height="200" alt ="" />
                <h2>{this.props.movie.name}</h2>
                <p>Date :{this.props.movie.date} </p>
                <Button onClick="Trailer()" >Watch Trailer</Button>

            </div>
        )
     }
 }
 export default Main
